import React, {useState} from 'react';
import auth0 from '../lib/auth0';

const CreateStatus = () => {
    const [dados, setDados] = useState({
        status:'bem',
        coords:{
            lat: null,
            long: null
        }
    })

    const getMyLocation = () =>{
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(position =>{
                
                setDados(old =>{
                    return{
                        ...old,
                        coords:{
                            lat: position.coords.latitude,
                            long: position.coords.longitude
                        }
                    }
                })
            })
        }
    }
    return (
        <div>
            <h1>Create Status</h1>
            <label className='block'>
                <input
                    type='radio'
                    name='status'
                    value='bem'
                    onClick={onStatusChange}
                />{' '}
                Estou bem sem sintomas.
            </label>
            <label className='block'><input type='radio' name='status' value='bem'/>Estou bem e sem sintomas.</label>
            <label className='block'><input type='radio' name='status' value='gripe'/>Estou com sintomas de gripe.</label>
            <label className='block'><input type='radio' name='status' value='covid'/>Estou com sintomas da COVID.</label>
            Sua posição atual: {lat} x {long}
            <button onClick={getMyLocation}>Get my location</button>
        </div>
    )
}

export default CreateStatus;


export async function getServerSideProps({req, res}){
    const session = await auth0.getSession(req);
    if(session){
        return{
            props:{
                isAuth: true,
                user:session.user,
                forceCreate
            }
        }
    }
    return{
        props:{
             isAuth:false,
            user:{}
        }
    }
}
